create or replace function f_crear_datos_holding() returns trigger as
$body$
declare
  aux numeric;
begin

/*
*/




  select nextval('cuco_seq')
  into   aux
  ;


  update cuentas_contables
  set    imputable = 'N'
  where  idgrem    = new.id
  and    segmento4 = '0000'
  ;

  update cuentas_contables
  set    cuenta_desplegable = segmento1 || '.'  || segmento2 || '.'  || segmento3 || '.'  || segmento4
  where  idgrem             = new.id
  ;


  return new;
end;
$body$ LANGUAGE plpgsql;

\q

